// 给对象添加类  1.点击的对象  2.类名  3.是否清除所有-非零[可选]
function addClass(obj,className,allt){
		if(allt>0){
		$(obj).click(function(){
				$(obj).removeClass(className);
				$(this).addClass(className);
			})
			}else{
					$(obj).click(function(){
					$(this).addClass(className).siblings().removeClass(className);
			})
				}
	}

addClass(".nav ul li","acti","");

$(function(){
	$(".ico-menu").click(function(event) {
		$(this).toggleClass('acti');
		if($(this).hasClass('acti')){
			$(".nav").fadeIn().removeClass('ac');
		}else{
			$(".nav").fadeOut();
		}
	});
	$(".nav ul li").click(function(event) {
		$(".ico-menu").removeClass('acti');
		$(".nav").addClass('ac');
	});
})